mapper.select:


    e.is_deleted = 0
    and e.sender_id = #{senderId}
    and e.receiver_id = #{receiverId}
    and e.folder = #{folder}
    and e.subject like concat('%', #{subject}, '%')
    and date_format(e.receive_time,'%Y-%m-%d') >= date_format(#{params.beginTime},'%Y-%m-%d')
    and date_format(e.receive_time,'%Y-%m-%d') <= date_format(#{params.endTime},'%Y-%m-%d')

order by e.receive_time desc

mapper.update:
update im_email
subject = #{subject}, text_content = #{textContent}, html_content = #{htmlContent}, is_read = #{isRead}, is_starred = #{isStarred}, folder = #{folder}, is_deleted = #{isDeleted},
where id = #{id}

mapper.select:
select count(*) from im_email

    is_deleted = 0
    and folder = #{folder}
    and is_read = #{isRead}
