mapper.select:


    ga.status = 1
    and ga.group_id = #{groupId}
    and ga.sender_id = #{senderId}
    and ga.type = #{type}
    and ga.is_pinned = #{isPinned}

order by ga.is_pinned desc, ga.create_time desc

mapper.update:
update im_group_announcement
content = #{content}, type = #{type}, attachment_url = #{attachmentUrl}, is_pinned = #{isPinned}, status = #{status}, update_time = #{updateTime}, expire_time = #{expireTime},
where id = #{id}
