mapper.select:


    and b.group_id = #{groupId}
    and b.user_id = #{userId}
    and b.operator_id = #{operatorId}
    and b.type = #{type}
    and b.is_active = #{isActive}
    and g.name like concat('%', #{groupName}, '%')
    and u.username like concat('%', #{userName}, '%')

        and date_format(b.create_time,'%Y-%m-%d') >= date_format(#{params.beginTime},'%Y-%m-%d')


        and date_format(b.create_time,'%Y-%m-%d') <= date_format(#{params.endTime},'%Y-%m-%d')


order by b.create_time desc

mapper.update:
update im_group_blacklist
group_id = #{groupId}, user_id = #{userId}, operator_id = #{operatorId}, type = #{type}, reason = #{reason}, expire_time = #{expireTime}, is_active = #{isActive}, update_time = #{updateTime},
where id = #{id}
