mapper.select:


    m.is_deleted = 0
    and m.conversation_id = #{conversationId}
    and m.sender_id = #{senderId}
    and m.message_type = #{messageType}
    and m.sensitive_level = #{sensitiveLevel}
    and m.content like concat('%', #{content}, '%')
    and date_format(m.create_time,'%Y-%m-%d') >= date_format(#{params.beginTime},'%Y-%m-%d')
    and date_format(m.create_time,'%Y-%m-%d') <= date_format(#{params.endTime},'%Y-%m-%d')

order by m.create_time desc


where m.conversation_id = #{conversationId}
and m.create_time between #{startTime} and #{endTime}
and m.is_deleted = 0
order by m.create_time desc

select count(*) from im_message

    is_deleted = 0
    and conversation_id = #{conversationId}
    and sender_id = #{senderId}
    and sensitive_level = #{sensitiveLevel}

mapper.update:
update im_message
content = #{content}, file_url = #{fileUrl}, file_name = #{fileName}, sensitive_level = #{sensitiveLevel}, is_edited = #{isEdited}, edited_content = #{editedContent}, edit_count = #{editCount}, edit_time = #{editTime}, update_time = #{updateTime},
where id = #{id}
