mapper.select:
select count(*) from im_todo_item
and user_id = #{userId}
and status = 'COMPLETED' and status in ('PENDING', 'IN_PROGRESS')

select count(*) from im_system_notification

     and receiver_id = #{userId} 
    and is_read = 0

select id, conversation_id as conversationId, sender_id as senderId, content, message_type as messageType, create_time as createTime
from im_message

     and sender_id = #{userId} 

order by create_time desc
 limit #{limit}
