mapper.select:




        and (username like concat('%', #{keyword}, '%')
        or nickname like concat('%', #{keyword}, '%')
        or mobile like concat('%', #{keyword}, '%')
        or email like concat('%', #{keyword}, '%'))

     and username like concat('%', #{username}, '%') 
     and nickname like concat('%', #{nickname}, '%') 
     and email like concat('%', #{email}, '%') 
     and mobile like concat('%', #{mobile}, '%') 
     and status = #{status} 
     and gender = #{gender} 


             and last_online_time >= DATE_SUB(NOW(), INTERVAL 5 MINUTE) 
             and (last_online_time < DATE_SUB(NOW(), INTERVAL 5 MINUTE) or last_online_time is null) 




        and date(create_time) >= #{params.beginTime}


        and date(create_time) <= #{params.endTime}



        and date(last_online_time) >= #{params.lastLoginBegin}


        and date(last_online_time) <= #{params.lastLoginEnd}




             and avatar is not null and avatar != '' 
             and (avatar is null or avatar = '') 



order by create_time desc

mapper.update:
update im_user
username = #{username}, password = #{password}, nickname = #{nickname}, email = #{email}, mobile = #{mobile}, avatar = #{avatar}, status = #{status}, gender = #{gender}, signature = #{signature}, last_online_time = #{lastOnlineTime}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select case when count(*) > 0 then true else false end
from im_user
where username = #{username}

select count(*) from (
    select 1 from im_conversation_member where user_id = #{userId}
    limit 1
) as t
