mapper.select:
select a.id, a.template_id, a.title, a.applicant_id, a.status, a.current_node_id,
       a.form_data, a.attachments, a.apply_time, a.finish_time,
       a.create_time, a.update_time, a.remark,
       u.nickname as applicant_name
from im_approval a
left join im_user u on a.applicant_id = u.id

    and a.template_id = #{templateId}
    and a.applicant_id = #{applicantId}
    and a.status = #{status}

order by a.create_time desc

mapper.update:
update im_approval
status = #{status}, current_node_id = #{currentNodeId}, form_data = #{formData}, attachments = #{attachments}, finish_time = #{finishTime}, update_by = #{updateBy}, remark = #{remark},
where id = #{id}
