mapper.select:



        AND title like concat('%', #{title}, '%')


        AND status = #{status}


        AND applicant_id = #{applicantId}


        AND date_format(apply_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(apply_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


order by apply_time desc

mapper.update:
update im_approval
template_id = #{templateId}, title = #{title}, applicant_id = #{applicantId}, status = #{status}, current_node_id = #{currentNodeId}, form_data = #{formData}, attachments = #{attachments}, apply_time = #{applyTime}, finish_time = #{finishTime}, remark = #{remark}, update_time = sysdate()
where id = #{id}

update im_approval set status = #{action}, remark = #{comment},
    update_time = NOW()
where id = #{id}
