mapper.update:
update im_file_asset
file_name = #{fileName}, file_type = #{fileType}, file_size = #{fileSize}, file_ext = #{fileExtension}, file_path = #{filePath}, md5 = #{md5}, uploader_id = #{uploaderId}, download_count = #{downloadCount}, download_expire_time = #{downloadExpireTime}, status = #{status},
where id = #{id}

mapper.select:
select count(*) from im_file_asset
where date(create_time) = curdate() and status = 'ACTIVE'
 and uploader_id = #{uploaderId}
