select.where:
and g.is_deleted = 0
 and g.name like concat('%', #{name}, '%') 
 and g.owner_id = #{ownerId} 
 and g.all_muted = #{allMuted}

mapper.update:
update im_group
name = #{name}, avatar = #{avatar}, description = #{description}, update_time = NOW(),
where id = #{id}

mapper.select:
select id, group_id, user_id, nickname, role, is_muted, is_deleted, deleted_time, create_time, update_time
from im_group_member
where group_id = #{groupId}

mapper.insert:
insert into im_group_member (group_id, user_id, role, nickname, is_muted, is_deleted, create_time, update_time)
select #{arg0}, #{arg1}, #{arg2}, COALESCE(u.nickname, u.username), 0, 0, NOW(), NOW()
from im_user u where u.id = #{arg1}
