mapper.select:


    and word like concat('%', #{word}, '%')
    and word_type = #{wordType}
    and level = #{level}
    and action = #{action}
    and is_enabled = #{isEnabled}

order by level desc, id asc

mapper.update:
update im_sensitive_word
word = #{word}, word_type = #{wordType}, level = #{level}, action = #{action}, replacement = #{replacement}, is_enabled = #{isEnabled},
where id = #{id}
