mapper.select:
SELECT * FROM im_message 
WHERE conversation_id = #{conversationId}

    AND id < #{lastMessageId}

ORDER BY create_time DESC

    LIMIT #{limit}

SELECT * FROM im_message 
WHERE conversation_id = #{conversationId}

    AND sender_id = #{userId}


    AND id < #{lastMessageId}

ORDER BY create_time DESC

    LIMIT #{limit}

SELECT * FROM im_message 
WHERE id = #{messageId}

    AND (sender_id = #{userId} OR conversation_id IN (
        SELECT conversation_id FROM im_conversation_member WHERE user_id = #{userId}
    ))

SELECT * FROM im_message 
WHERE 1=1

    AND conversation_id = #{conversationId}


    AND content LIKE CONCAT('%', #{keyword}, '%')


    LIMIT #{limit}

SELECT * FROM im_message 
WHERE 1=1

    AND conversation_id = #{conversationId}


    AND sender_id = #{userId}


    AND content LIKE CONCAT('%', #{keyword}, '%')


    AND type = #{messageType}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

ORDER BY create_time DESC

SELECT * FROM im_message 
WHERE sender_id = #{senderId}

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    LIMIT #{limit}

ORDER BY create_time DESC

SELECT * FROM im_message 
WHERE sender_id = #{senderId}

    AND type = #{messageType}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

ORDER BY create_time DESC

mapper.update:
UPDATE im_message 
SET status = #{status}
WHERE id IN

    #{id}

UPDATE im_message 
SET status = #{status}
WHERE id IN

    #{id}

UPDATE im_message 
SET status = 'REVOKED', is_revoked = 1, update_time = #{revokedTime}
WHERE id = #{messageId}

mapper.select:
SELECT * FROM im_message 
WHERE 1=1

    AND sensitive_level = #{sensitiveLevel}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    LIMIT #{limit}

ORDER BY create_time DESC

SELECT * FROM im_message 
WHERE conversation_id = #{conversationId}
AND type = 'FILE'

    LIMIT #{limit}

ORDER BY create_time DESC

SELECT COUNT(*) FROM im_message 
WHERE sender_id = #{senderId}

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

SELECT DATE(create_time) as date, COUNT(*) as count FROM im_message WHERE 1=1
AND create_time >= #{startTime}
AND create_time <= #{endTime}
GROUP BY DATE(create_time) ORDER BY date

SELECT COUNT(*) FROM im_message 
WHERE conversation_id = #{conversationId}
AND sender_id != #{userId}
AND id > #{lastReadMessageId}
